def test_tx_deserialiazation():
    for test in tests:
        test_data = test
        test = bytes.fromhex(test["hex"])
        deser = tx_lib.DeserializerHorizen(test)
        tx = deser.read_tx()

        # transaction version
        assert tx.version == test_data["raw_tx"]["version"]

        # inputs
        for index, input in enumerate(tx.inputs):
            assert hash_to_hex_str(input.prev_hash) == test_data["raw_tx"]["vin"][index]["txid"]
            assert input.prev_idx == test_data["raw_tx"]["vin"][index]["vout"]
            assert input.script.hex() == test_data["raw_tx"]["vin"][index]["scriptSig"]["hex"]
            assert input.sequence == test_data["raw_tx"]["vin"][index]["sequence"]

        # outputs
        for index, output in enumerate(tx.outputs):
            assert f'{output.value * 1e-8:.8f}' == test_data["raw_tx"]["vout"][index]["value"]
            assert output.pk_script.hex() == test_data["raw_tx"]["vout"][index]["scriptPubKey"]["hex_without_replay"]

        # locktime 
        assert tx.locktime == test_data["raw_tx"]["locktime"]
        